<script setup lang="ts">
import { IonContent, IonPage, IonGrid, IonRow, IonCol } from '@ionic/vue';
import { useDataStore } from '@/stores/data';
import BaseNav from '@/components/BaseNav.vue';
import SocialIcons from '@/components/SocialIcons.vue';
import FairKeepInTouchForm from '@/components/Fair/KeepInTouchForm.vue';
import FairBottomNavigation from '@/components/Tabs/FairBottomNavigation.vue';
import fairLogo from '@/imgs/svg/fair-logo-light.svg';
import appConfig from '@/config/app';
import { useLogger } from '@/composables/useLogger';

const logger = useLogger();
const dataStore = useDataStore();

const isHeaderVisible = ref(false);
let lastScrollY = 0;

const carouselContainer = ref<HTMLElement | null>(null);
const hamburgerMenuBaseNav = ref<InstanceType<typeof BaseNav> | null>(null);

// Sample sponsors data - replace with your actual data
const sponsors = computed(() => dataStore.data.nysfairWebsite.sponsors || []);

const handleImageError = (event: Event) => {
  const img = event.target as HTMLImageElement;
  img.src = '/path-to-fallback-image.jpg'; // Replace with your fallback image
};

// Carousel functionality
let startX = 0;
let scrollLeft = 0;
let isDown = false;

const openTicketsWebsite = () => {
  if (!dataStore.nysfairTicketsUrl) {
    return;
  }

  window.open(dataStore.nysfairTicketsUrl);
};

const handleScroll = (event: CustomEvent) => {
  // const currentY = event.detail.currentY;

  // const isScrollingDown = currentY > lastScrollY;
  // const isScrollingUp = currentY < lastScrollY;

  // // Only trigger if scrolled past 10px
  // if (currentY > 10) {
  //   if (isScrollingDown) {
  //     isHeaderVisible.value = true; // Show header when scrolling down
  //   } else if (isScrollingUp) {
  //     isHeaderVisible.value = false; // Hide header when scrolling up
  //   }
  // } else {
  //   isHeaderVisible.value = false; // At top, keep it hidden
  // }

  // lastScrollY = currentY;
};

const onBottomNavTabClick = ({ id }: { id: string }) => {
  switch (id) {
    case 'hamburgerMenu': {
      // Open the hamburger menu
      if (hamburgerMenuBaseNav.value) {
        hamburgerMenuBaseNav.value.openMenu();
      } else {
        logger.warn('hamburgerMenuBaseNav is not ready or missing');
      }

      break;
    }
    default: {
      logger.warn('Unknown tab clicked', {
        ID: id,
      });

      break;
    }
  }
};

onMounted(() => {
  const container = carouselContainer.value;
  if (!container) return;

  container.addEventListener('mousedown', (e: MouseEvent) => {
    isDown = true;
    startX = e.pageX - container.offsetLeft;
    scrollLeft = container.scrollLeft;
  });

  container.addEventListener('mouseleave', () => {
    isDown = false;
  });

  container.addEventListener('mouseup', () => {
    isDown = false;
  });

  container.addEventListener('mousemove', (e: MouseEvent) => {
    if (!isDown) return;
    e.preventDefault();
    const x = e.pageX - container.offsetLeft;
    const walk = (x - startX) * 2;
    container.scrollLeft = scrollLeft - walk;
  });
});
</script>

